import React, {useState} from "react";

function CreateArea(props) {
const [titleText, setTitleText] = useState("");
const [contentText, setContentText] = useState("");

function titleChange(event){
  const titleValue = event.target.value;
  setTitleText((prevValue) =>{
    return {...prevValue, titleValue}
  });
};

function contentChange(event){
  const contentValue = event.target.value;
  setContentText((prevValue) =>{
    return {...prevValue, contentValue}
  })
}

  return (
    <div>
      <form>
        <input onChange={titleChange} name="title" placeholder="Title" />
        <textarea onChange={contentChange} name="content" placeholder="Take a note..." rows="3" />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
